var bodyListItems=document.querySelectorAll(".sort-list-wrapper .body .body-list p");var header=document.querySelector(".sort-list-wrapper .header");var body=document.querySelector(".sort-list-wrapper .body");bodyListItems[0].classList.add("selected");body.style.maxHeight="0";body.style.overflow="hidden";header.addEventListener("click",function(){if(body.style.maxHeight==="0px"){body.style.maxHeight=body.scrollHeight+"px"}else{body.style.maxHeight="0"}header.classList.toggle("open");body.classList.toggle("show")});bodyListItems.forEach(function(item){item.addEventListener("click",function(){bodyListItems.forEach(function(item){item.classList.remove("selected")});this.classList.add("selected");var currentSortName=this.textContent;var headerSpan=document.querySelector(".sort-list-wrapper .header span");headerSpan.textContent=currentSortName;body.style.maxHeight="0";header.classList.remove("open");body.classList.remove("show")})});